const red=document.querySelector("#red"),green=document.querySelector("#green"),blue=document.querySelector("#blue");function getSliderValue(){return{r:red.value,g:green.value,b:blue.value}}function changeSliderValue(e){e.oninput=function(){const e=document.querySelector(".resultColor"),l=getSliderValue().r,r=getSliderValue().g,u=getSliderValue().b;e.style.backgroundColor=`rgb(${l}, ${r}, ${u})`}}function changeAllSliderValues(){this.changeSliderValue(red),this.changeSliderValue(green),this.changeSliderValue(blue)}changeAllSliderValues();